private void BasicAboutBox_Click(object sender, RoutedEventArgs e)
{
    var aboutBox = new AboutBox("Modular Config Utility", "Version:  1.0", "2014", new Uri("pack://application:,,/App.ico"));
    aboutBox.ShowModal(this);
